import 'package:equatable/equatable.dart';import 'package:meta/meta.dart';@immutableabstract class WeatherEvent extends Equatable {}class GetWeatherEvent extends WeatherEvent {  final city;  GetWeatherEvent({this.city});  @override  List<Object> get props => [city];  @override  String toString() {    return 'GetWeatherEvent{city: $city}';  }}class SaveWeatherEvent extends WeatherEvent {  @override  List<Object> get props => [];  @override  String toString() {    return 'SaveWeatherEvent{}';  }}