import 'package:equatable/equatable.dart';import 'package:meta/meta.dart';@immutableabstract class WeatherState extends Equatable {}class InitialWeatherState extends WeatherState {  @override  List<Object> get props => [];}class WeatherLoadingState extends WeatherState {  @override  List<Object> get props => [];}class WeatherLoadedState extends WeatherState {  final weather;  WeatherLoadedState(this.weather);  @override  List<Object> get props => [weather];}class WeatherLoadingErrorState extends WeatherState {  final error;  WeatherLoadingErrorState({this.error});  @override  List<Object> get props => [error];}