import 'package:flutter/material.dart';import 'package:sunshine/config/routes.dart';import 'package:sunshine/screen/weather_screen.dart';import 'package:sunshine/widgets/show_weather.dart';class RouteGenerator {  static Route<dynamic> generateRoute(RouteSettings settings) {    // Getting arguments passed in while calling Navigator.pushNamed    final args = settings.arguments;    switch (settings.name) {      case MAIN_SCREEN:        return MaterialPageRoute(            builder: (_) => WeatherSearchScreen());      case WEATHER_SCREEN:        return MaterialPageRoute(            builder: (_) => ShowWeatherScreen(args));      default:      // If there is no such named route in the switch statement, e.g. /third        return _errorRoute();    }  }  static Route<dynamic> _errorRoute() {    return MaterialPageRoute(builder: (_) {      return Scaffold(        appBar: AppBar(          title: Text('Error'),        ),        body: Center(          child: Text('ERROR'),        ),      );    });  }}