import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:sunshine/backend/bloc.dart';import 'package:sunshine/repository/weather_repo.dart';import 'package:sunshine/widgets/city_weather.dart';import 'package:sunshine/widgets/world_spinner.dart';class WeatherScreen extends StatelessWidget {  @override  Widget build(BuildContext context) {    return Scaffold(        appBar: AppBar(          centerTitle: true,          title: Text('Weather for city'),        ),        body: Container(          child: ListView(            children: <Widget>[              WorldSinner(),              BlocProvider(                create: (context) => WeatherBloc(WeatherRepository()),                child: WeatherForm(),              ),            ],          ),        ));  }}