import 'package:flutter/material.dart';class SearchRaisedButton extends StatelessWidget {  final String _buttonText;  final VoidCallback _voidCallback;  final bool _isEnable;  SearchRaisedButton(      {Key key, String buttonText, VoidCallback voidCallback, bool isEnable})      : _buttonText = buttonText,        _voidCallback = voidCallback,        _isEnable = isEnable,        super(key: key);  @override  Widget build(BuildContext context) {    return Padding(      padding: const EdgeInsets.all(8.0),      child: Container(        height: 50,        width: double.infinity,        child: RaisedButton(          disabledColor: Colors.grey,          color: Colors.indigo,          child: Text(            this._buttonText,            style: TextStyle(                fontWeight: FontWeight.bold, fontSize: 20, color: Colors.white),          ),          onPressed: this._isEnable ?  null: _voidCallback,        ),      ),    );  }}