import 'package:flutter/material.dart';import 'package:sunshine/models/weather.dart';class ShowWeatherScreen extends StatelessWidget {  final WeatherModel weather;  ShowWeatherScreen(this.weather);  getIcon(final icon) {    var weatherPicture;    switch (icon) {      case '01d':        weatherPicture = 'assets/01d.png';        break;      case '01n':        weatherPicture = 'assets/01n.png';        break;      case '02d':        weatherPicture = 'assets/02d.png';        break;      case '02n':        weatherPicture = 'assets/02n.png';        break;      case '03d':        weatherPicture = 'assets/03d.png';        break;      case '03n':        weatherPicture = 'assets/03n.png';        break;      case '04d':        weatherPicture = 'assets/04d.png';        break;      case '04n':        weatherPicture = 'assets/04n.png';        break;      case '09d':        weatherPicture = 'assets/09d.png';        break;      case '09n':        weatherPicture = 'assets/09n.png';        break;      case '10d':        weatherPicture = 'assets/10d.png';        break;      case '10n':        weatherPicture = 'assets/10n.png';        break;      case '11d':        weatherPicture = 'assets/11d.png';        break;      case '11n':        weatherPicture = 'assets/11n.png';        break;      case '13d':        weatherPicture = 'assets/13d.png';        break;      case '13n':        weatherPicture = 'assets/13n.png';        break;      case '50d':        weatherPicture = 'assets/50d.png';        break;      case '50n':        weatherPicture = 'assets/50n.png';        break;    }    return weatherPicture;  }  @override  Widget build(BuildContext context) {    getIcon(weather);    return Container(      child: SingleChildScrollView(        child: Column(          children: <Widget>[            Container(              child: Column(                children: <Widget>[                  Text(                    weather.name,                    style: TextStyle(                        fontSize: 20,                        fontWeight: FontWeight.bold,                        color: Colors.black),                  ),                  Column(                    children: weather.weather                        .map((e) => Column(                              children: <Widget>[                                Image.asset(getIcon(e.icon)),                                Text(e.description),                              ],                            ))                        .toList(),                  )                ],              ),            ),            Padding(              padding: const EdgeInsets.all(8.0),              child: Card(                child: ListTile(                  title: Text('TEMP. C : ${weather.main.temp.toString()}'),                ),              ),            ),            Padding(              padding: const EdgeInsets.all(4.0),              child: Card(                child: ListTile(                  title: Text('TEMP. Min. C : ${weather.main.tempMin.toString()}'),                ),              ),            ),            Padding(              padding: const EdgeInsets.all(4.0),              child: Card(                child: ListTile(                  title: Text('TEMP. Min. C : ${weather.main.tempMax.toString()}'),                ),              ),            ),            Padding(              padding: const EdgeInsets.all(4.0),              child: Card(                child: ListTile(                  title: Text('HUMIDITY: ${weather.main.humidity}'),                ),              ),            ),            Padding(              padding: const EdgeInsets.all(4.0),              child: Card(                child: ListTile(                  title: Text('PRESURE: ${weather.main.pressure}'),                ),              ),            ),            Padding(              padding: const EdgeInsets.all(4.0),              child: Card(                child: ListTile(                  leading: Image.asset('assets/50d.png'),                  title: Text('Wind Speed: ${weather.wind.speed}'),                ),              ),            ),          ],        ),      ),    );  }}