import 'package:flutter/material.dart';import 'package:sunshine/screen/weather_screen.dart';class TryAgain extends StatelessWidget {  final String errorText;  TryAgain({this.errorText});  @override  Widget build(BuildContext context) {    return Container(      child: Column(        children: <Widget>[          Text(            errorText,            style: TextStyle(fontSize: 15, color: Colors.orange),          ),          IconButton(            icon: Icon(Icons.refresh,color: Colors.blue,size: 30,),            onPressed: () {              Navigator.pop(context);              Navigator.push(                context,                MaterialPageRoute(builder: (context) => WeatherScreen()),              );            },          )        ],      ),    );  }}