import 'package:flutter/material.dart';import 'package:sunshine/screen/weather_screen.dart';class WeatherButton extends StatelessWidget {  @override  Widget build(BuildContext context) {    return GestureDetector(        child: Padding(          padding: const EdgeInsets.all(8.0),          child: Container(              height: 50,              decoration: BoxDecoration(                  borderRadius: BorderRadius.circular(9.0),                  color: Colors.orange),              alignment: Alignment.center,              child: Text("Check weather for city",                  style: TextStyle(fontSize: 20, fontWeight: FontWeight.bold))),        ),        onTap: () {          Navigator.push(            context,            MaterialPageRoute(builder: (context) => WeatherScreen()),          );        });  }}